function burgerMenu(){let e=document.querySelector(".hamburger"),t=document.querySelector(".nav-menu");e.addEventListener("click",(()=>{e.classList.toggle("active"),t.classList.toggle("active")})),document.querySelectorAll(".burger-menu_link").forEach((c=>c.addEventListener("click",(()=>{e.classList.remove("active"),t.classList.remove("active")}))))}burgerMenu();